  //       setLoading(false);
  //     }
  //   };

  //   fetchData();
  // }, []);

  const numberOfMessages = data.length;

  const numberOfUnreadMessages = data.reduce(
    (accumulator, currentValue, index) => {
      if (!currentValue.isRead) {
        accumulator += 1;
      } else {
        accumulator += 0;
      }
      return accumulator;
    },
    0
  );

  return (
    <Card>
      <div className={styles.container}>
        <p>Hello Jim</p>
        <h3>
          You have {`${numberOfUnreadMessages}`} unread messages out of{" "}
          {`${numberOfMessages}`} total
        </h3>
        <Link to="/inbox" className={styles.button}>
          View Messages
        </Link>
      </div>
    </Card>
  );
};

export default HomePage;
