import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Card from "../components/UI/Card/Card";
import useApi from "../useApi/useApi";
import axios from "axios";
import styles from "./HomePage.module.css";

const HomePage = ({ data }) => {
  // const { loading, messageContent } = useApi("/");
  const [user, setUser] = useState({});
  const [loading, setLoading] = useState(true);

  // const { loading, messageContent: message } = useApi(`/api/messages/${id}`);

  useEffect(() => {
    const fetchData = async () => {
      const { users } = await axios.get("/");
      console.log(users);
      if (users) {
        setUser(users);
        setLoading(false);
      }
    };

    fetchData();
  }, []);
  const numberOfMessages = data.length;

  const numberOfUnreadMessages = data.reduce(
    (accumulator, currentValue, index) => {
      if (!currentValue.isRead) {
        accumulator += 1;
      } else {
        accumulator += 0;
      }
      return accumulator;
    },
    0
  );

  return (
    <Card>
      <div className={styles.container}>
        <p>Hello {user.name}</p>
        <h3>
          You have {`${numberOfUnreadMessages}`} unread messages out of{" "}
          {`${numberOfMessages}`} total
        </h3>
        <Link to="/inbox" className={styles.button}>
          View Messages
        </Link>
      </div>
    </Card>
  );
};

export default HomePage;
