import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Card from "../components/UI/Card/Card";
import useApi from "../useApi/useApi";
import axios from "axios";
import styles from "./HomePage.module.css";

const HomePage = () => {
  // const { loading, messageContent } = useApi("/");
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // const { loading, messageContent: users } = useApi("/");

  useEffect(() => {
    const fetchData = async () => {
      const { data } = await axios.get("/");
      console.log(data);
      if (data) {
        setUser(data);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  console.log(user);
  const numberOfMessages = user.users.length;

  const numberOfUnreadMessages = user.users.reduce(
    (accumulator, currentValue, index) => {
      if (!currentValue.isRead) {
        accumulator += 1;
      } else {
        accumulator += 0;
      }
      return accumulator;
    },
    0
  );

  return (
    <Card>
      <div className={styles.container}>
        <p>Hello {`${user.users[0].firstName}`}</p>
        <h3>
          You have {`${numberOfUnreadMessages}`} unread messages out of{" "}
          {`${numberOfMessages}`} total
        </h3>
        <Link to="/inbox" className={styles.button}>
          View Messages
        </Link>
      </div>
    </Card>
  );
};

export default HomePage;
